import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
function Read() {
  const localStorageKey ="curd"
  const [curd,setCurd]= useState(()=>{
    return JSON.parse(localStorage.getItem(localStorageKey))
    ||[]  })
  useEffect(()=>{
    localStorage.setItem(localStorageKey,JSON.stringify(curd))
  },[curd])

  const addCurd=(data)=>{
   setCurd([...contact,{id: uuid4(),data}])
  }
  const  handleDelete =(id)=>{
     const updatedList=curd.filter((val)=>{
      return val.id !== id;  
     })
     setCurd(updatedList)
  }
