import React, { useRef, useState } from 'react'
import './CURD.css'
function CRUD(){
    
        const list=[
            {
                id:1,
                name:"HP",
                price:"111"
            },
            {
                id:2,
                name:"Dell",
                price:"222"
            },
            
        ]
        const[lists, setList]= useState(list)
        const[updateState,setUpdateState]=useState(-1)
        return(
            <div className="crud">
              
              <div>
                  <AddList setList={setList}/>
                  <form>
            <table>
            {
                lists.map((current)=>(
                    updateState===current.id ? <EditList  current={current} list={list} setList={setList} />: 
                
                        
                    <tr>
                    <td>{current.name}</td>
                    <td>{current.price}</td>
                    <td>
                        <button className='edit' onClick={()=> handleSubmit(current.id)} >Edit</button>
                        <button className='delete'>Delete</button>
                    </td>
                 
                    </tr>
                ))
            }
            </table>
                    </form>
            </div>
        </div>
        )

function handleSubmit(id){
    setUpdateState(id)
}
}

function EditList({current}){
    function handInput(event){
        const name=event.target.name;
        const value = name.value;
    }
    return(
        <tr>
            <td><input type="text" onChange={handInput} name="name" value={current.name}/></td>
            <td><input type="text"name="price" value={current.price}/></td>
            <td><button type="submit">Update</button></td>
        
        </tr>
    )
}


function AddList({setList}){

    const nameRef=useRef();
    const priceRef=useRef();

    function handleSubmit(event){
        event.preventDefault();
        const name=event.target.elements.name.value;
        const price=event.target.elements.price.value;
         const newlist={
            id:3,
            name, 
            price
         }
    setList((prevList)=>{
        return prevList.concat(newlist)
    })
    nameRef.current.value=""
    priceRef.current.value=""
 
    }
    return(
        <form className='addForm' onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Enter Name" ref={nameRef}/>
            <input type="text" name="price" placeholder="Enter Price"ref={priceRef}/>
            <button type="submit">Add</button>
        </form>
    )
}

export default CRUD;
