import React, { useState } from "react"
import { Link } from "react-router-dom"
import Validation from "./SignupValidation";
function App() {
  const [values, setValues]=useState({
    name:"",
    email:"",
    passwprd:""
  })
    const [errors,setErrors]=useState({})
    const handleInput=(event)=>{
        setValues(prev=>({...prev,[event.target.name]:[event.target.value]}))

    }
    const handleSubmit=(event)=>{
        event.preventDefault();
        setErrors(Validation(values));
    }
  return (
    <div className="d-flex justify-content-center align-item-center bg-primary vh-100">
        <div className="bg-white p-3 rounded flex-wrap-reverse w-25 h-50">
          <h2>Sign-Up</h2>
            <form action="" onSubmit={handleSubmit}>
                <div className="mb-3">
                    <label htmlFor="name"><strong>Name</strong></label>
                    <input type="text" placeholder="Enter Name" name="name"
                    onChange={handleInput} className="form-control rounded-0"/>
                    {errors.name && <span className="text-danger"> {errors.name}</span>}
                    </div>
                <div className="mb-3">
                    <label htmlFor="email"><strong>Email</strong></label>
                    <input type="email" placeholder="Enter Email" name="email"
                    onChange={handleInput} className="form-control rounded-0"/>
                    {errors.email && <span className="text-danger"> {errors.email}</span>}
                </div>
                <div className="mb-3">
                    <label htmlFor="password"><strong>Password</strong></label>
                    <input type="password" placeholder="Enter Password" name="password"
                    onChange={handleInput} className="form-control rounded-0"/>
                    {errors.password && <span className="text-danger"> {errors.password}</span>}
                </div>
                <button className="btn btn-success w-100 rounded-0">Sign in</button>

                <p>You are agree to our teams and policies</p>
                <Link to="/" className="btn btn-default border w-100 bg-light rounded-0 text-decoration-none">Log in</Link>

            </form>
        </div>
        </div>
  );
}

export default App;
